package array;import java.util.HashSet;public class ArrayExample1 {    int[] intArray = new int[10];    String[] strArray = new String[10];    String[] strConstArray = {"siba","Prasad","Mohanty"};    public static void main(String[] args) {        printTwoSumCombinationInArray(new int[]{1, 2, 3, 4, 5}, 6);    }    private static void printTwoSumCombinationInArray(int[] array, int sum){        HashSet<Integer> hashSetUniqueCombination = new HashSet<>();        for(int item : array){            int otherItem = sum - item;            if(!hashSetUniqueCombination.contains(otherItem)){                hashSetUniqueCombination.add(otherItem);            }            else{                System.out.println(otherItem+" "+item);            }        }    }}