package array;import java.util.Arrays;public class SecondLargestNumberInArray {    public static void main(String[] args) {        int[] array = {4, 3, 2, 6, 7, 8, 9, 1, 2};        getNthLargestNumberFrom(array, 2);        print2largest(array, 2);    }    private static int getNthLargestNumberFrom(int[] array, int nth) {        Arrays.sort(array);        return array[array.length - nth];    }    public static void print2largest(int arr[], int arr_size) {        int i, first, second;        /* There should be atleast two elements */        if (arr_size < 2) {            System.out.print(" Invalid Input ");            return;        }        first = second = Integer.MIN_VALUE;        for (i = 0; i < arr_size; i++) {            /* If current element is greater than            first then update both first and second */            if (arr[i] > first) {                second = first;                first = arr[i];            }            /* If arr[i] is in between first and               second then update second  */            else if (arr[i] > second && arr[i] != first)                second = arr[i];        }        if (second == Integer.MIN_VALUE)            System.out.print("There is no second largest"                    + " element\n");        else            System.out.print("The second largest element"                    + " is " + second);    }}