package kotlinprograms.codingProgramsfun main() {    println("${findFactorialOfNumber(4)}")    println("${areAnagrams("Silent", "Listen")}")}fun findFactorialOfNumber(factNum: Int): Int {    var num = factNum    if (num <= 1) return 1    var fact = 1    while (num > 0) {        fact *= num        num--    }    return fact}fun areAnagrams(str1: String, str2: String): Boolean {    if (str1.length != str2.length) {        return false    }    val charCount = IntArray(26)    for (i in str1.indices) {        charCount[str1[i] - 'a']++        charCount[str2[i] - 'a']--    }    for (count in charCount) {        if (count != 0) {            return false        }    }    return true}